            payload:data
                })
                localStorage.setItem('userInfo',JSON.stringify(data))
    } catch (error) {
        dispatch({
            type : USER_REGISTER_FAIL , payload : error.response && error.response.data.message ? error.response.data.message : error.message 
        })
    }
    }

